% TSSAVEEXPERIMENT   Save an experiment from the .mat file%   TSSAVEEXPERIMENT(FILENAME) saves the experiment to FILENAME%%	See also TSLOADEXPERIMENT, TSEXPORTCODES% Updated: 10/7/2009 by Randy Gallistelfunction [result] = TSsaveexperiment(filename)result = 0;if evalin('base','isempty(who(''global'',''Experiment''))')    error('There is no experiment structure');    return;end;global Experiment;if nargin < 1    filename = Experiment.Name;else    if all(filename(end-3:end) == '.mat')        filename(end-3:end) = [];    end    if ~strmatch(filename, Experiment.Name)        warning('TSsaveexperiment:ExperimentRenamed','Experiment.Name has been changed from %s to %s.', Experiment.Name, filename);        Experiment.Name = filename;    endend                %v = version;%if (str2num(v(1)) > 6)%evalin('base',['save ' filename ' Experiment' ' -V6']);%elseevalin('base',['save ''' filename ''' Experiment']);    %endresult = 1;